package sample;
public class Forstarrev1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int line=10;
		
		for(int b=10;b>=1;b--) 
		{
			for(int a=(line-1);a>=b;a--) //space		
			{
				System.out.print(" ");
			}
			for(int a=1;a<=b;a++)  //start
			{
				System.out.print("*");
			}
			System.out.println();
			
		}
			
			
			
		}
		
	}


