package sample;

public class Forstarreverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int b=1;b<=10;b++) {
			for(int a=9;a>=b;a--) {
				System.out.print(" ");
			}
				for(int a=1;a<=b;a++) {
					System.out.print("*");
				}
				
				System.out.println();
		}

	}

}
