package sample;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=45;
		byte b=56;
		int x=78;
		int c=a+b;
		System.out.println(c);
		boolean d=a<b;
		System.out.println(d);
		boolean f=a>x;
		boolean t=d&&f;
		System.out.println(t);
		System.out.println(a);
		System.out.println(b);
		a=b;
		System.out.println(a);
		System.out.println(b);



}

}
